    let style;

    if (props.state.submitFail) {
        style={border: '.3vh solid red'}
    }

    return (
        <label className='formElement' id='emailLabel'>
            Email
            <input 
                id='emailElement'
                type='text'
                name='email'
                value={props.state.userInput.email}
                onChange={props.state.handleChange}
                style=''>
            </input>
        </label>
    )
}
