import AddressInput from './AddressInput';
import EmailInput from './EmailInput';
import NameInput from './NameInput';
import PhoneInput from './PhoneInput';


export default function Stage1 (props) {
    let display;

    (props.stage === 1) ? display = 'grid' : display = 'none';

    function handleSubmit () {
        let name = Document.getElementByID('nameElement').value
        let address1 = Document.getElementByID('addressElement1').value
        let address2 = Document.getElementByID('addressElement2').value
        let city = Document.getElementByID('city').value
        let state = Document.getElementByID('state').value
        let zip = Document.getElementByID('zip').value
        let phone = Document.getElementByID('phoneElement').value
        let email = Document.getElementByID('emailElement').value

        props.setUserInput({
            name: name,
            address1: address1,
            address2: address2,
            city: city,
            state: state,
            zip: zip,
            phone: phone,
            email: email,
            ...props.userInput
        })

    }

    return (
        <form className='formProper' style={{display: `${display}`}} onSumbit={handleSubmit}>
            <p className='stageName'>Shipping Address</p>
            <NameInput/>
            <AddressInput stage={props.stage}/>
            <PhoneInput/>
            <EmailInput/>
            <button className='submit' type='submit'>Next</button>
        </form>
    )
}
